package factorial.gradle;

import org.junit.jupiter.api.Test;


class FactorialTest {
    void shouldReturnOneForZero() {
        int actual_fact, expected_fact=1;
        FactorialTest factorialFinder = new FactorialTest();
        actual_fact = factorialFinder.calculateFactorial(0);
        assert actual_fact==expected_fact;
    }

    @Test
    void shouldReturnOneForOne() {

        FactorialTest factorialFinder = new FactorialTest();
        assert factorialFinder.calculateFactorial(1) == 1;
    }

    @Test
    void shouldReturnOneTwentyForFive() {

        FactorialTest factorialFinder = new FactorialTest();
        assert factorialFinder.calculateFactorial(5) == 120;
    }
     public int calculateFactorial(int number) {

        int result = 1;
        if (number == 0 || number == 1) return result;

        for (int x = 1; x <= number; x++) result *= x;
        return result;
    }
}
